cat >> "c:\Users\IZUCHUKWU MCGIBSON\OneDrive\Desktop\my_projects\my_portfolio\index.css" <<EOF

/* Contact Section */
.contact-section {
  padding: 4rem 2rem;
  max-width: 800px;
  margin: 0 auto;
}

.contact-title {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: #000;
  letter-spacing: -0.02em;
}

.contact-description {
  font-size: 1rem;
  color: #666;
  line-height: 1.6;
  margin-bottom: 2.5rem;
}

.contact-form {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.form-row {
  display: flex;
  gap: 1.5rem;
}

.form-row input {
  flex: 1;
}

input,
textarea {
  width: 100%;
  padding: 1rem 1.5rem;
  background: #f5f5f5;
  border: 1px solid transparent;
  border-radius: 12px;
  font-family: inherit;
  font-size: 1rem;
  color: #333;
  outline: none;
  transition: border-color 0.2s ease, background 0.2s ease;
}

input:focus,
textarea:focus {
  background: #fff;
  border-color: #ddd;
}

textarea {
  resize: vertical;
  min-height: 150px;
}

.submit-btn {
  width: 100%;
  padding: 1rem;
  border: none;
  border-radius: 12px;
  font-family: inherit;
  font-size: 1rem;
  font-weight: 600;
  color: #fff;
  cursor: pointer;
  background-color: #000;
  /* Doodle pattern */
  background-image: url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M10 10 Q 30 5 50 10 T 90 10' stroke='%23333' fill='none' stroke-width='2'/%3E%3Ccircle cx='20' cy='40' r='5' stroke='%23333' fill='none' stroke-width='2'/%3E%3Crect x='60' y='30' width='10' height='10' stroke='%23333' fill='none' stroke-width='2' transform='rotate(20 65 35)'/%3E%3Cpath d='M10 80 L 30 80 L 20 60 Z' stroke='%23333' fill='none' stroke-width='2'/%3E%3Cpath d='M50 60 C 60 50 80 50 90 60' stroke='%23333' fill='none' stroke-width='2'/%3E%3Cpath d='M70 80 L 90 90' stroke='%23333' fill='none' stroke-width='2'/%3E%3C/svg%3E");
  background-size: 100px 100px;
  transition: transform 0.2s ease, opacity 0.2s ease;
  position: relative;
  overflow: hidden;
}

.submit-btn:hover {
  transform: translateY(-2px);
  opacity: 0.9;
}

@media (max-width: 600px) {
  .form-row {
    flex-direction: column;
    gap: 1rem;
  }
}
EOF
